import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ModalService {
  menssagem: string = '';

  constructor() { }

  add(menssagem: string) {
    this.menssagem = menssagem;

    setTimeout(() => {
      this.clear();
    }, 4000);
  }

  clear() {
    this.menssagem = '';
  }
}
