import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators } from '@angular/forms';

import { Usuario } from '../../../../Models/Usuario';
import { Contato } from '../../../../Models/Contato';

import { UsuarioServiceService } from '../../../../Services/Usuario/usuario-service.service';
import { ContatoServiceService } from '../../../../Services/Contato/contato-service.service';

@Component({
  selector: 'app-new-contato',
  templateUrl: './new-contato.component.html',
  styleUrls: ['./new-contato.component.css']
})
export class NewContatoComponent {
  usuarios!: Usuario[];
  contatoForm!: FormGroup;
  contato!: Contato;
  constructor(
    private usuarioService: UsuarioServiceService,
    private contatoService: ContatoServiceService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.usuarioService.BuscarTodos().subscribe(response => {
      this.usuarios = response.data;
    });

    this.contatoForm = new FormGroup({
      id: new FormControl(0),
      nome: new FormControl('', [Validators.required]),
      email: new FormControl('', [Validators.required, Validators.email]),
      celular: new FormControl('', [Validators.required, Validators.pattern(/^-?\d+$/)]),
      usuarioId: new FormControl('', [Validators.required])
    });
  }

  get nome() {
    return this.contatoForm.get('nome')!;
  }
  get email() {
    return this.contatoForm.get('email')!;
  }
  get celular() {
    return this.contatoForm.get('celular')!;
  }
  get usuarioId() {
    return this.contatoForm.get('usuarioId')!;
  }

  submit() {
    if (this.contatoForm.invalid) {
      return;
    }

    this.adicionar(this.contatoForm.value);
  }

  async adicionar(contato: Contato) {
    if (typeof contato.usuarioId === 'string') {
      contato.usuarioId = parseInt(contato.usuarioId);
    }

    await this.contatoService.Adicionar(contato).subscribe(response => {
      if (response.data) {
        this.router.navigate(['/contato']);
        console.log('response.data: ', response.data);
        //MENSSAGEM q foi adicionado
      }
      else {
        console.log('response.data: ', response.data);
        // MENSSAGEM q nao foi adicionado;
      }
    });
  }
}
<div class="m-5">
  <h2 class="text-center">Adicionar Contato</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="contatoForm" #formDir="ngForm">
    <input type="hidden" formControlName="id">
    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-2 w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" placeholder="Digite seu nome" required>
        <div *ngIf="nome.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="nome.errors?.['required']">O nome é obrigatório!</p>
        </div>
      </div>

      <div class="form-group mb-2 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" placeholder="Digite seu email" required>
        <div *ngIf="email.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="email.errors?.['required']">O email é obrigatório!</p>
          <p *ngIf="email.errors?.['email']">Este campo só aceita emails válidos!</p>
        </div>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-2 w-25">
        <label for="celular" class="form-label">Celular:</label>
        <input type="text" class="form-control" formControlName="celular" placeholder="Digite seu celular" required>
        <div *ngIf="celular.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="celular.errors?.['required']">O celular é obrigatório!</p>
          <p *ngIf="celular.errors?.['pattern']">Este campo só aceita números!</p>
        </div>
      </div>

      <div class="form-group mb-2 w-25">
        <label for="usuarioId" class="form-label">Usuário:</label>
        <select class="form-select" formControlName="usuarioId" aria-label="Selecione o usuário">
          <option value="" disabled selected hidden>Selecione</option>
          <option *ngFor="let usuario of usuarios" value="{{usuario.id}}">{{usuario.nome}}</option>
        </select>
        <div *ngIf="usuarioId.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="usuarioId.errors?.['required']">O usuário é obrigatório!</p>
        </div>
      </div>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Adicionar</button>
      <a class="btn btn-secondary" routerLink="/contato">Voltar</a>
    </div>
  </form>
</div>
