import { Component, Input } from '@angular/core';
import { BsModalRef } from 'ngx-bootstrap/modal';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  //@Input() title: string;
  //@Input() msg: string;
  //@Input() cancelTxt: string = 'Cancelar';
  //@Input() confirmTxt: string = 'Confirmar';

  //constructor(public bsModalRef: BsModalRef) { }

  //ngOnInit() { }

  //onClose() {
  //  this.bsModalRef.hide();
  //}

  //onConfirm() {
  //  this.bsModalRef.hide();
  //}
}
<div class="modal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">{{title}}</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>{{msg}}</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal" (click)="onClose()">{{cancelTxt}}</button>
        <button type="button" class="btn btn-primary" (click)="onConfirm()">{{confirmTxt}}</button>
      </div>
    </div>
  </div>
</div>
