import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ModalService {
  menssagem: string = '';
  btnConfirmar: boolean = false;

  constructor() { }

  add(menssagem: string) {
    this.menssagem = menssagem;
  }

  clear() {
    this.menssagem = '';
  }

  confirmar() {
    this.btnConfirmar = true;
    this.clear();
  }
}
