import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Usuario } from '../../../../../Models/Usuario';
import { UsuarioServiceService } from '../../../../../Services/Usuario/usuario-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-identificacao-usuario',
  templateUrl: './identificacao-usuario.component.html',
  styleUrls: ['./identificacao-usuario.component.css']
})
export class IdentificacaoUsuarioComponent {
  identificarUsuarioForm!: FormGroup;
  usuario!: Usuario;

  constructor(
    private usuarioService: UsuarioServiceService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.identificarUsuarioForm = new FormGroup({
      login: new FormControl('', [Validators.required]),
      email: new FormControl('', [Validators.required, Validators.email]),
    });
  }

  get login() {
    return this.identificarUsuarioForm.get('login')!;
  }
  get email() {
    return this.identificarUsuarioForm.get('email')!;
  }

  submit() {
    if (this.identificarUsuarioForm.invalid) {
      return;
    }

    this.buscarPorEmailELogin(this.email.value, this.login.value);
  }

  async buscarPorEmailELogin(email: string, login: string) {
    await this.usuarioService.BuscarPorEmailELogin(email, login).subscribe(response => {
      if (response.data) {
        this.usuario = response.data;

        this.router.navigate([`/alterarSenha/${this.usuario.id}`]);
      }
      else {
        this.router.navigate([`/IdentificacaoUsuario`]);
        // MENSSAGEM de erro, falando q nao encontrou usuario;
      }
    });
  }
}
<div class="m-5">
    <h2 class="text-center">Identificar Usuário</h2>

    <hr>

    <form (ngSubmit)="submit()" [formGroup]="identificarUsuarioForm" #formDir="ngForm">
      <div class="d-flex justify-content-evenly">
        <div class="form-group mb-2 w-25">
          <label for="login" class="form-label">Login:</label>
          <input type="text" class="form-control" formControlName="login" required>
          <div *ngIf="login.invalid && formDir.submitted" class="erro-validacao">
            <p *ngIf="login.errors?.['required']">O login é obrigatório!</p>
          </div>
        </div>
      </div>

      <div class="d-flex justify-content-evenly">
        <div class="form-group mb-4 w-25">
          <label for="email" class="form-label">E-mail:</label>
          <input type="text" class="form-control" formControlName="email" required>
          <div *ngIf="email.invalid && formDir.submitted" class="erro-validacao">
            <p *ngIf="email.errors?.['required']">O email é obrigatório!</p>
            <p *ngIf="email.errors?.['email']">Este campo só aceita emails válidos!</p>
          </div>
        </div>
      </div>

      <br />

      <div class="d-flex justify-content-center">
        <button type="submit" class="btn btn-primary me-2">Identificar</button>
        <a class="btn btn-secondary" routerLink="/">Voltar</a>
      </div>
    </form>
  </div>
