import { Component } from '@angular/core';

import { FormGroup, FormControl, Validators } from '@angular/forms';

import { Usuario } from 'src/app/Models/Usuario';
import { UsuarioServiceService } from '../../../../Services/Usuario/usuario-service.service';

@Component({
  selector: 'app-new-usuario',
  templateUrl: './new-usuario.component.html',
  styleUrls: ['./new-usuario.component.css']
})
export class NewUsuarioComponent {
  usuarioForm!: FormGroup;

  constructor(private usuarioService: UsuarioServiceService) { }

  ngOnInit(): void {
    this.usuarioForm = new FormGroup({
      id: new FormControl(''),
      nome: new FormControl('', [Validators.required]),
      login: new FormControl('', [Validators.required]),
      email: new FormControl('', [Validators.required]),
      perfil: new FormControl(''),
      senha: new FormControl('', [Validators.required]),
    });
  }

  get nome() {
    return this.usuarioForm.get('nome')!;
  }
  get login() {
    return this.usuarioForm.get('login')!;
  }
  get email() {
    return this.usuarioForm.get('email')!;
  }
  get perfil() {
    return this.usuarioForm.get('perfil')!;
  }
  get senha() {
    return this.usuarioForm.get('senha')!;
  }

  async adicionar(usuario: Usuario) {
    const formData = new FormData();

    formData.append('nome', usuario.nome);
    formData.append('login', usuario.login);
    formData.append('email', usuario.email);
    formData.append('senha', usuario.senha);

    if (usuario.perfil != 0) {
      formData.append('perfil', usuario.perfil);
    }

    await this.usuarioService.Adicionar(formData).subscribe();
  }

  submit() {
    if (this.usuarioForm.invalid) {
      return;
    }

    this.adicionar(this.usuarioForm.value);
    console.log("add");
    console.log(this.usuarioForm.value);
  }
}
<div class="m-5">
  <h2 class="text-center">Adicionar Usuário</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="usuarioForm" #formDir="ngForm">
    <input type="hidden" formControlName="id">
    <div class="d-flex justify-content-evenly">
      <div class="mb-2 w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" placeholder="Digite seu nome" required>
        <div *ngIf="nome.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="nome.errors?.['required']">O nome é obrigatório!</p>
        </div>
      </div>

      <div class="mb-2 w-25">
        <label for="login" class="form-label">Login:</label>
        <input type="text" class="form-control" formControlName="login" placeholder="Digite seu login" required>
        <div *ngIf="login.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="login.errors?.['required']">O login é obrigatório!</p>
        </div>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="mb-2 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" placeholder="Digite seu email" required>
        <div *ngIf="email.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="email.errors?.['required']">O email é obrigatório!</p>
        </div>
      </div>

      <div class="mb-4 w-25">
        <label for="senha" class="form-label">Senha:</label>
        <input type="password" class="form-control" formControlName="senha" placeholder="Digite sua senha" required>
        <div *ngIf="senha.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="senha.errors?.['required']">A senha é obrigatória!</p>
        </div>
      </div>
    </div>

    <div class="w-50 mx-auto">
      <select class="form-select" aria-label="Selecione">
        <option selected>Selecione</option>
        <option value="1">Administrador</option>
        <option value="2">Padrão</option>
      </select>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Adicionar</button>
      <a class="btn btn-secondary" routerLink="/usuario">Voltar</a>
    </div>
  </form>
</div>
