import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

import { Usuario } from 'src/app/Models/Usuario';
import { Response } from 'src/app/Models/Response';

import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class UsuarioServiceService {
  private baseApiUrl = environment.baseApiUrl;
  private apiUrl = `${this.baseApiUrl}api/Usuario`;

  constructor(private http: HttpClient) { }

  BuscarTodos(): Observable<Response<Usuario[]>> {
    return this.http.get<Response<Usuario[]>>(this.apiUrl);
  }

  BuscarPorId(id: number): Observable<Response<Usuario>> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.get<Response<Usuario>>(url);
  }

  Adicionar(formData: FormData): Observable<FormData> {
    return this.http.post<FormData>(this.apiUrl, formData);
  }

  Atualizar(formData: FormData): Observable<FormData> {
    return this.http.put<FormData>(this.apiUrl, formData);
  }

  Apagar(id: number) {
    const url = `${this.apiUrl}/${id}`;
    return this.http.delete(url);
  }

  AlterarSenha(formData: FormData): Observable<FormData> {
    const url = `${this.apiUrl}/AlterarSenhaUsuario`;
    return this.http.put<FormData>(url, formData);
  }
}
