import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { Usuario } from '../../../../Models/Usuario';
import { UsuarioSemSenha } from '../../../../Models/UsuarioSemSenha';

import { UsuarioServiceService } from '../../../../Services/Usuario/usuario-service.service';

@Component({
  selector: 'app-atualizar-usuario',
  templateUrl: './atualizar-usuario.component.html',
  styleUrls: ['./atualizar-usuario.component.css']
})
export class AtualizarUsuarioComponent {
  usuario!: Usuario;
  atualizarUsuarioForm!: FormGroup;
  constructor(
    private usuarioService: UsuarioServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));

    this.usuarioService.BuscarPorId(id).subscribe((response) => {
      this.usuario = response.data;
    });

    this.atualizarUsuarioForm = new FormGroup({
      id: new FormControl(id),
      nome: new FormControl(this.usuario.nome),
      login: new FormControl(this.usuario.login),
      email: new FormControl(this.usuario.email),
      perfil: new FormControl( this.usuario.perfil),
    });
  }

  submit() {
    if (this.atualizarUsuarioForm.invalid) {
      return;
    }

    this.atualizar(this.atualizarUsuarioForm.value);
  }

  async atualizar(usuario: UsuarioSemSenha) {
      await this.usuarioService.Atualizar(usuario).subscribe();
      this.router.navigate(['/usuario']);
      // MENSSAGEM q foi add
  }
}
<div class="m-5">
  <h2 class="text-center">Atualizar Usuário</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="atualizarUsuarioForm" #formDir="ngForm">
    <input type="hidden" formControlName="id">
    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-2 w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" required>
      </div>

      <div class="form-group mb-2 w-25">
        <label for="login" class="form-label">Login:</label>
        <input type="text" class="form-control" formControlName="login" required>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-3 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" required>
      </div>
    </div>

    <div class="form-group w-50 mx-auto">
      <select class="form-select" formControlName="perfil" aria-label="Selecione">
        <option value="" disabled selected hidden>Selecione</option>
        <option value="1">Administrador</option>
        <option value="2">Padrão</option>
      </select>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Editar</button>
      <a class="btn btn-secondary" routerLink="/usuario">Voltar</a>
    </div>
  </form>
</div>
