import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ModalService {
  message: string = '';
  btnCancelar: boolean = false;
  btnConfirmar: boolean = false;


  public confirmarApagarContato: boolean = false
  public confirmarApagarContatoObservable = new BehaviorSubject(this.confirmarApagarContato);
  _confirmarApagarContato = this.confirmarApagarContatoObservable.asObservable();

  constructor() { }

  add(message: string) {
    this.message = message;

    //setTimeout(() => {
    //  this.clear();
    //}, 4000);
  }

  clear() {
    this.message = '';
  }

  cancelar() {
    this.btnCancelar = true;
    this.clear();
  }

  confirmar() {
    this.btnConfirmar = true;
    this.clear();
  }

}
