import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ModalService {
  message: string = '';
  btnCancelar: boolean = false;
  btnConfirmar: boolean = false;

  constructor() { }

  add(message: string) {
    this.message = message;

    setTimeout(() => {
      this.clear();
    }, 4000);
  }

  clear() {
    this.message = '';
  }

  cancelar() {
    this.btnCancelar = true;
  }

  confirmar() {
    this.btnConfirmar = true;
  }
}
