import { Component, OnInit } from '@angular/core';
import { ModalService } from '../../../Services/Menssagens/Modal/modal.service';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent implements OnInit {
  constructor(public modalService: ModalService) { }

  ngOnInit(): void {
    this.modalService.modalConfirmEvent.subscribe(() => {
      // Lógica a ser executada ao confirmar
      console.log('Ação confirmada!');
    });

    this.modalService.modalCancelEvent.subscribe(() => {
      // Lógica a ser executada ao cancelar
      console.log('Ação cancelada!');
    });
  }
}
<div class="modalStyle bg-secondary-subtle p-3 mt-4 rounded" tabindex="-1"> <!--*ngIf="modalService.menssagem"-->
  <div class="d-flex justify-content-between">
    <h5>Menssagem do Sistema:</h5>
    <button type="button" class="btn-close" (click)="modalService.fecharModal()"></button>
  </div>
  <div class=" my-2">
    <p>{{modalService.menssagem}}ddddddddddddd</p>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-secondary me-2" (click)="modalService.cancelar()">Cancelar</button>
    <button type="button" class="btn btn-primary" (click)="modalService.confirmar()">Confirmar</button>
  </div>
</div>
