import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AlertService {
  menssagem: string = '';

  constructor() { }

  addErro(menssagem: string) {
    this.menssagem = menssagem;
  }

  addSucesso(menssagem: string) {
    this.menssagem = menssagem;
  }

  clear() {
    this.menssagem = '';
  }
}
