import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { Contato } from '../../../../Models/Contato';

import { ContatoServiceService } from '../../../../Services/Contato/contato-service.service';

@Component({
  selector: 'app-atualizar-contato',
  templateUrl: './atualizar-contato.component.html',
  styleUrls: ['./atualizar-contato.component.css']
})
export class AtualizarContatoComponent {
  contato!: Contato;
  atualizarContatoForm!: FormGroup;

  constructor(
    private contatoService: ContatoServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));

    this.contatoService.BuscarPorId(id).subscribe((response) => {
      this.contato = response.data;

      this.atualizarContatoForm = new FormGroup({
        id: new FormControl(id),
        nome: new FormControl(this.contato.nome, [Validators.required]),
        email: new FormControl(this.contato.email, [Validators.required]),
        celular: new FormControl(this.contato.celular, [Validators.required, Validators.pattern(/^-?\d+$/)]),
        usuarioId: new FormControl(this.contato.usuarioId, [Validators.required, Validators.pattern(/^-?\d+$/)])
      });
    });
  }

  submit() {
    if (this.atualizarContatoForm.invalid) {
      return;
    }

    this.atualizar(this.atualizarContatoForm.value);
  }

  async atualizar(contato: Contato) {
    if (typeof contato.usuarioId === 'string') {
      contato.usuarioId = parseInt(contato.usuarioId);
    }

    await this.contatoService.Atualizar(contato).subscribe(response => {
      if (response.data) {
        this.router.navigate(['/contato']);
        console.log('response.data: ', response.data);
        //MENSSAGEM q foi atualizada
      }
      else {
        console.log('response.data: ', response.data);
        // MENSSAGEM q nao foi atualizada;
      }
    });
  }
}
<div class="m-5">
  <h2 class="text-center">Atualizar Contato</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="atualizarContatoForm" #formDir="ngForm">
    <input type="hidden" formControlName="id">
    <div class="d-flex justify-content-evenly">
      <div class="form-group w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" required>
        <div *ngIf="nome.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="nome.errors?.['required']">O nome é obrigatório!</p>
        </div>
      </div>

      <div class="form-group mb-4 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" required>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-4 w-25">
        <label for="email" class="form-label">Celular:</label>
        <input type="text" class="form-control" formControlName="celular" required>
      </div>

      <div class="form-group mb-4 w-25">
        <label for="usuarioId" class="form-label">UsuarioId:</label>
        <input type="text" class="form-control" formControlName="usuarioId" required>
      </div>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Editar</button>
      <a class="btn btn-secondary" routerLink="/contato">Voltar</a>
    </div>
  </form>
</div>
