import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AlertService {
  message: string = '';
  btnCancelar: boolean = false;
  btnConfirmar: boolean = false;

  constructor() { }

  addErro(message: string) {
    this.message = message;
  }

  addSucesso(message: string) {
    this.message = message;
  }

  clear() {
    this.message = '';
  }

  cancelar() {
    this.btnCancelar = true;
    this.clear();
  }

  confirmar() {
    this.btnConfirmar = true;
    this.clear();
  }
}
