import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { Usuario } from '../../../../Models/Usuario';
import { UsuarioSemSenha } from '../../../../Models/UsuarioSemSenha';

import { UsuarioServiceService } from '../../../../Services/Usuario/usuario-service.service';

@Component({
  selector: 'app-atualizar-usuario',
  templateUrl: './atualizar-usuario.component.html',
  styleUrls: ['./atualizar-usuario.component.css']
})
export class AtualizarUsuarioComponent {
  usuario!: Usuario;
  atualizarUsuarioForm!: FormGroup;
  constructor(
    private usuarioService: UsuarioServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));

    this.atualizarUsuarioForm = new FormGroup({
      id: new FormControl(id),
      nome: new FormControl('', [Validators.required]),
      login: new FormControl('', [Validators.required]),
      email: new FormControl('', [Validators.required, Validators.email]),
      perfil: new FormControl('', [Validators.required])
    });

    this.usuarioService.BuscarPorId(id).subscribe((response) => {
      this.usuario = response.data;
      console.log('response.data: ', response.data);

      this.atualizarUsuarioForm.patchValue({
        nome: this.usuario.nome,
        login: this.usuario.login,
        email: this.usuario.email,
        perfil: this.usuario.perfil
      });
    });
  }

  get nome() {
    return this.atualizarUsuarioForm.get('nome')!;
  }
  get login() {
    return this.atualizarUsuarioForm.get('login')!;
  }
  get email() {
    return this.atualizarUsuarioForm.get('email')!;
  }
  get perfil() {
    return this.atualizarUsuarioForm.get('perfil')!;
  }
  get senha() {
    return this.atualizarUsuarioForm.get('senha')!;
  }

  submit() {
    if (this.atualizarUsuarioForm.invalid) {
      return;
    }

    this.atualizar(this.atualizarUsuarioForm.value);
  }

  async atualizar(usuario: UsuarioSemSenha) {
    if (typeof usuario.perfil === 'string') {
      usuario.perfil = parseInt(usuario.perfil);
    }

    await this.usuarioService.Atualizar(usuario).subscribe(response => {
      if (response.data) {
        this.router.navigate(['/usuario']);
        console.log('response.data: ', response.data);
        //MENSSAGEM q foi atualizada
      }
      else {
        console.log('response.data: ', response.data);
        // MENSSAGEM q nao foi atualizada;
      }
    });
  }
}
<div class="m-5">
  <h2 class="text-center">Atualizar Usuário</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="atualizarUsuarioForm" #formDir="ngForm">
    <input type="hidden" formControlName="id">
    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-2 w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" required>
        <div *ngIf="nome.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="nome.errors?.['required']">O nome é obrigatório!</p>
        </div>
      </div>

      <div class="form-group mb-2 w-25">
        <label for="login" class="form-label">Login:</label>
        <input type="text" class="form-control" formControlName="login" required>
        <div *ngIf="login.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="login.errors?.['required']">O login é obrigatório!</p>
        </div>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-4 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" required>
        <div *ngIf="email.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="email.errors?.['required']">O email é obrigatório!</p>
          <p *ngIf="email.errors?.['email']">Este campo só aceita emails válidos!</p>
        </div>
      </div>
    </div>

    <div class="form-group w-50 mx-auto">
      <select class="form-select" formControlName="perfil" aria-label="Selecione">
        <option *ngIf="usuario.perfil == null" value="0" disabled selected hidden>Selecione</option>
        <option *ngIf="usuario.perfil === 1" value="1" disabled selected hidden>Adminitrador</option>
        <option *ngIf="usuario.perfil === 2" value="2" disabled selected hidden>Padrão</option>
        <option value="1">Administrador</option>
        <option value="2">Padrão</option>
      </select>
      <div *ngIf="perfil.invalid && formDir.submitted" class="erro-validacao">
        <p *ngIf="perfil.errors?.['required']">O perfil é obrigatória!</p>
      </div>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Editar</button>
      <a class="btn btn-secondary" routerLink="/usuario">Voltar</a>
    </div>
  </form>
</div>
