import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { Usuario } from '../../../../Models/Usuario';
import { Contato } from '../../../../Models/Contato';

import { UsuarioServiceService } from '../../../../Services/Usuario/usuario-service.service';
import { ContatoServiceService } from '../../../../Services/Contato/contato-service.service';

@Component({
  selector: 'app-atualizar-contato',
  templateUrl: './atualizar-contato.component.html',
  styleUrls: ['./atualizar-contato.component.css']
})
export class AtualizarContatoComponent implements OnInit {
  usuarios!: Usuario[];
  atualizarContatoForm!: FormGroup;
  contato!: Contato;

  constructor(
    private usuarioService: UsuarioServiceService,
    private contatoService: ContatoServiceService,
    private route: ActivatedRoute,
    private router: Router,
  ) { }

  //ngOnInit(): void {
  //  this.usuarioService.BuscarTodos().subscribe(response => {
  //    this.usuarios = response.data;
  //  });

  //  const id = Number(this.route.snapshot.paramMap.get('id'));

  //  this.atualizarContatoForm = new FormGroup({
  //    id: new FormControl(id),
  //    nome: new FormControl('', [Validators.required]),
  //    email: new FormControl('', [Validators.required, Validators.email]),
  //    celular: new FormControl('', [Validators.required, Validators.pattern(/^[1-9]\d*$/)]),
  //    usuarioId: new FormControl('', [Validators.required])
  //  });

  //  this.contatoService.BuscarPorId(id).subscribe((response) => {
  //    this.contato = response.data;
  //    console.log('response.data: ', response.data);

  //    this.atualizarContatoForm.patchValue({
  //      nome: this.contato.nome,
  //      email: this.contato.email,
  //      celular: this.contato.celular,
  //      usuarioId: this.contato.usuarioId
  //    });
  //  });
  //}

  ngOnInit(): void {
    this.usuarioService.BuscarTodos().subscribe(response => {
      this.usuarios = response.data;
    });

    const id = Number(this.route.snapshot.paramMap.get('id'));

    this.contatoService.BuscarPorId(id).subscribe((response) => {
      this.contato = response.data;
      console.log('response.data: ', response.data);

      this.atualizarContatoForm = new FormGroup({
        id: new FormControl(id),
        nome: new FormControl(this.contato.nome, [Validators.required]),
        email: new FormControl(this.contato.email, [Validators.required, Validators.email]),
        celular: new FormControl(this.contato.celular, [Validators.required, Validators.pattern(/^[1-9]\d*$/)]),
        usuarioId: new FormControl(this.contato.usuarioId, [Validators.required])
      });
    });
  }

  get nome() {
    return this.atualizarContatoForm.get('nome')!;
  }
  get email() {
    return this.atualizarContatoForm.get('email')!;
  }
  get celular() {
    return this.atualizarContatoForm.get('celular')!;
  }
  get usuarioId() {
    return this.atualizarContatoForm.get('usuarioId')!;
  }

  submit() {
    if (this.atualizarContatoForm.invalid) {
      return;
    }

    this.atualizar(this.atualizarContatoForm.value);
  }

  async atualizar(contato: Contato) {
    if (typeof contato.usuarioId === 'string') {
      contato.usuarioId = parseInt(contato.usuarioId);
    }

    await this.contatoService.Atualizar(contato).subscribe(response => {
      if (response.data) {
        this.router.navigate(['/contato']);
        console.log('response.data: ', response.data);
        //MENSSAGEM q foi atualizada
      }
      else {
        console.log('response.data: ', response.data);
        // MENSSAGEM q nao foi atualizada;
      }
    });
  }
}
<div class="m-5">
  <h2 class="text-center">Atualizar Contato</h2>

  <hr>

  <form (ngSubmit)="submit()" [formGroup]="atualizarContatoForm" #formDir="ngForm">
    <input type="hidden" formControlName="id" />
    <div class="d-flex justify-content-evenly">
      <div class="form-group w-25">
        <label for="nome" class="form-label">Nome:</label>
        <input type="text" class="form-control" formControlName="nome" required>
        <div *ngIf="nome.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="nome.errors?.['required']">O nome é obrigatório!</p>
        </div>
      </div>

      <div class="form-group mb-4 w-25">
        <label for="email" class="form-label">E-mail:</label>
        <input type="text" class="form-control" formControlName="email" required>
        <div *ngIf="email.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="email.errors?.['required']">O email é obrigatório!</p>
          <p *ngIf="email.errors?.['email']">Este campo só aceita emails válidos!</p>
        </div>
      </div>
    </div>

    <div class="d-flex justify-content-evenly">
      <div class="form-group mb-4 w-25">
        <label for="celular" class="form-label">Celular:</label>
        <input type="text" class="form-control" formControlName="celular" required>
        <div *ngIf="celular.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="celular.errors?.['required']">O celular é obrigatório!</p>
          <p *ngIf="celular.errors?.['pattern']">Este campo só aceita números!</p>
        </div>
      </div>

      <div class="form-group mb-2 w-25">
        <label for="usuarioId" class="form-label">Usuário:</label>
        <select class="form-select" formControlName="usuarioId" aria-label="Selecione o usuário">
          <option value="" disabled selected hidden>Selecione</option>
          <option *ngFor="let usuario of usuarios" value="{{usuario.id}}">{{usuario.nome}}</option>
        </select>
        <div *ngIf="usuarioId.invalid && formDir.submitted" class="erro-validacao">
          <p *ngIf="usuarioId.errors?.['required']">O usuário é obrigatório!</p>
        </div>
      </div>
    </div>

    <br />

    <div class="d-flex justify-content-center">
      <button type="submit" class="btn btn-primary me-2">Editar</button>
      <a class="btn btn-secondary" routerLink="/contato">Voltar</a>
    </div>
  </form>
</div>
